INSERT INTO `html_portlet` VALUES (2,'<p>Affichage de la page <a href=\"jsp/site/Portal.jsp?page=appointmentsearch&amp;category=Avocats\">Cartographie</a> pour les services de rendez-vous détéritorrialisés</p>\n<p class=\"logo\">&nbsp;</p>'),(4,'<h2>Get into the Back Office of this demo site</h2> \n<p class=\"mb20\">Sign in as administrator with <em>admin/adminadmin</em>.</p> \n<p><a class=\"btn btn-primary\" title=\"Access to admin [Open  in new window]\" href=\"jsp/admin/AdminLogin.jsp\" target=\"\"><span class=\"fa fa-door-open\">&nbsp;</span> Enter Back Office</a></p> \n<p>&nbsp;</p>'),(5,'<h3>Resources</h3> \n<hr> \n<div class=\"row\"> \n <div class=\"col-xs-12 col-sm-6\"> \n  <div class=\"media\"> \n   <div class=\"media-left\">\n    <a title=\"GitHub site [Open  in new window]\" href=\"https://github.com/lutece-platform\" target=\"_blank\"><img class=\"media-object\" src=\"images/local/skin/github-logo.png\" alt=\"Github logo\" width=\"100px\"> </a>\n   </div> \n   <div class=\"media-body\"> \n    <h4 class=\"media-heading\">Access to our code repository</h4> \n   </div> \n  </div> \n  <div class=\"media\"> \n   <div class=\"media-left\">\n    <a title=\"Docker Hub site [Open  in new window]\" href=\"https://hub.docker.com/u/lutece\" target=\"_blank\"><img class=\"media-object\" src=\"images/local/skin/docker-logo.png\" alt=\"Docker images\" width=\"100px\"> </a>\n   </div> \n   <div class=\"media-body\"> \n    <h4 class=\"media-heading\">Grab some Docker images to run demos</h4> \n   </div> \n  </div> \n </div> \n <div class=\"col-xs-12 col-sm-6\"> \n  <div class=\"media\"> \n   <div class=\"media-left\">\n    <a title=\"Lutece Wiki site [Open  in new window]\" href=\"https://fr.lutece.paris.fr/fr/jsp/site/Portal.jsp?page=wiki&amp;action=changeLanguage&amp;page_name=home&amp;language=en\" target=\"_blank\"> <img class=\"media-object\" src=\"images/local/skin/wiki-logo.png\" alt=\"WIKI\" width=\"100px\"> </a>\n   </div> \n   <div class=\"media-body\"> \n    <h4 class=\"media-heading\">Read our technical documentation</h4> \n   </div> \n  </div> \n  <div class=\"media\"> \n   <div class=\"media-left\">\n    <a title=\" Twitter Site [Open  in new window]\" href=\"https://twitter.com/LuteceNews\" target=\"_blank\"> <img class=\"media-object\" src=\"images/local/skin/twitter-logo.png\" alt=\"Twitter logo\" width=\"100px\"> </a>\n   </div> \n   <div class=\"media-body\"> \n    <h4 class=\"media-heading\">Follow us on Twitter</h4> \n   </div> \n  </div> \n </div> \n</div>'),(7,'<div class=\"container\">\n <div class=\"row align-items-center\">\n  <div class=\"col-lg-6\">\n   <div class=\"lutece-content\">\n    <h1 class=\"my-3\">Software for Cities</h1>\n    <p class=\"font-weight-bold\">Lutece is an open source service platform that allows municipalities to share, reuse and adapt digital services created by other cities. Lutece is free, fully customizable and secure.<br>\n      With Lutece, discover more than 400 plugins and ready-to-use modules to power your digital services and meet users\' needs.<br>\n      You can easily customize them or create new digital modules and share them with the Lutece community.</p>\n   </div>\n   <div class=\"lutece-content mt-5\">\n    <h2 class=\"my-3\">Have a look at the admin tools</h2>\n    <p class=\"font-weight-bold\">Log to look at admin features, using <strong>\"admin\"</strong> as login and <strong>\"adminadmin\"</strong> as password</p>\n    <p class=\"text-center\"><a class=\"btn-solid-reg\" title=\"Go to admin login page [Open in new window]\" href=\"jsp/admin/AdminLogin.jsp\" target=\"_blank\"><br><span class=\"text\">Go to login page !</span><br></a></p>\n   </div>\n  </div>\n  <div class=\"col-lg-6\">\n   <br><img src=\"images/site/city-home.svg\" alt=\"\">\n  </div>\n </div>\n</div>');
